import java.util.Scanner;

public class Code_10951 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	 Scanner scan = new Scanner(System.in);
	 int A=0, B=0;
	 
	 while(scan.hasNextInt()) {
		 A = scan.nextInt();
		 B = scan.nextInt();
		 System.out.println(A+B);
	 }
	}

}
